import React,{ useContext } from 'react'
import { RecipeContext } from '../context/RecipeContext';
const DetailPage = ({match}) => {
    const { recipes } = useContext(RecipeContext);
    // const recipeImg = `https://spoonacular.com/recipeImages/${id}-480x360.jpg`
    let recipe = recipes.find(p => p.id ==match.params.recipeId)

    if (recipe) {
        recipeData = (
            <div>
                <h3>{recipe.name}</h3>
            </div>
        )
    }
    return (
        <div>

            {recipeData}
        </div>
    )
}

export default DetailPage
