import React, {useState, useEffect} from 'react'

import { useParams } from 'react-router-dom'
import axios from 'axios'
const DetailPage = () => {

    // const reci = recipes.find(item => item.id === match.params.recipeId)
    // const id = match..recipeId
    // console.log(id)
    const [recipe, setRecipe] = useState([])

    let { recipeId } = useParams();
    // const recipe = recipes.find(item => item.id===recipeId)
    // console.log(recipe)

    useEffect(() => {
        async function getData() {
            const request = await axios.get(`https://api.spoonacular.com/recipes/${recipeId}/information?includeNutrition=false`);
            setRecipe(request.data.results)

        }
        getData();
    },[recipe])
    console.log(recipe)

    return (
        <div>
            Now showing post {recipeId}

        </div>
    )
}

export default DetailPage
