import React from 'react'
import '../css/Incredient.css'
import {Avatar, ListItem, ListItemIcon, ListItemText,Box, Typography} from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles';
import { blue } from '@material-ui/core/colors';

const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex',
      '& > *': {
        margin: theme.spacing(1),
      },
    },
    name: {
        marginLeft: theme.spacing(5),

    },
    avatar: {
      color: theme.palette.getContrastText(blue[500]),
      backgroundColor: blue[500],
      width: theme.spacing(3),
      height: theme.spacing(3),
    },
    incredient: {
        display: "flex",
    }
    // purple: {
    //   color: theme.palette.getContrastText(deepPurple[500]),
    //   backgroundColor: deepPurple[500],
    // },

  }));
const Incredients = ({item}) => {
    const classes = useStyles();
    return (
        <Box className={classes.root}>
            <ListItem className={classes.root}>
                <ListItemIcon>
                    <Avatar variant="rounded" className={classes.avatar}>{item.amount}</Avatar>
                    <Typography>
                        {item.unit}
                    </Typography>
                </ListItemIcon>

                <ListItemText className={classes.name} primary={item.name} />



            </ListItem>


            {/* <li><span>{item.amount} {item.unit}</span>{item.name}</li> */}

        </Box>
    )
}

export default Incredients
