    useEffect(() => {
        async function getData() {
            const request = await axios.get(`https://spoonacular.com/recipeImages/${recipe.id}-{312x150}.{jpg}`);
            console.log(request)

        }
        getData();
    },[])

    return (
        <div className="recipe">
            <img src={recipeImg} alt='' />
            <h3>{recipe.title}</h3>
        </div>
    )
}

export default Recipe
